angular.module("mundialMath.resources").factory("mundialApi",["$http",function(r){var t="http://mundialmath.pythonanywhere.com";return{getUsers:function(){return r({url:t+"/users/score",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getInputs:function(a){return r({url:t+"/users/"+a,method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getInputsWizard:function(a){return r({url:t+"/wizard/"+a,method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getPartidos:function(){return r({url:t+"/partidos/actual",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getPartidosHistorico:function(){return r({url:t+"/partidos/historico",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},enviarOdds:function(a){r({url:t+"/users/"+a.usuario,method:"POST",params:a,crossDomain:!0})},actualizarResultado:function(a){r({url:t+"/partidos/"+a.id,method:"POST",params:a,crossDomain:!0})},guardarResultadosHistoricos:function(a){r({url:t+"/partidos/guardar",method:"POST",params:{tabla:JSON.stringify(a)},crossDomain:!0})},guardarResultadosUsuarios:function(a){r({url:t+"/users/guardar",method:"POST",params:{tabla:JSON.stringify(a)},crossDomain:!0})}}}]);