angular.module("mundialMath.resources").factory("mundialApi",["$http",function(r){var t="https://fierce-waters-19600.herokuapp.com";return{getUsers:function(){return r({url:t+"/users/score",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getInputs:function(e){return r({url:t+"/users/"+e,method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getInputsWizard:function(e){return r({url:t+"/wizard/"+e,method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getPartidos:function(){return r({url:t+"/partidos/actual",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},getPartidosHistorico:function(){return r({url:t+"/partidos/historico",method:"GET",withCredentials:!1,crossDomain:!0,eventHandlers:{}})},enviarOdds:function(e){r({url:t+"/users/"+e.usuario,method:"POST",params:e,crossDomain:!0})},actualizarResultado:function(e){r({url:t+"/partidos/"+e.id,method:"POST",params:e,crossDomain:!0})},guardarResultadosHistoricos:function(r){console.log(r)}}}]);